INSERT INTO `emailinfo` VALUES ('user_signup_mail', 'Welcome to [SITE_NAME]', 'Welcome to [SITE_NAME]<p>Please keep this e-mail for your records. Your account information is as follows:</p><p>----------------------------<br />Username: [USERNAME]<br />Password: [PASSWORD]<br />----------------------------</p><p>Your account is currently inactive. You cannot use it until you visit the following link:</p><p><a href="[VERIFY_LINK]">[VERIFY_LINK]</a></p><p>Please do not forget your password as it has been encrypted in our database and we cannot retrieve it for you. However, should you forget your password you can request a new one which will be activated in the same way as this account.</p><p>Thank you for registering. --</p><p>Thanks,</p><p><a href="[SITE_URL]">[SITE_NAME]</a> Team</p>', 'Welcome Mail For New User');
INSERT INTO `emailinfo` VALUES ('invite_friends', 'Invitation from  [SENDER_NAME]', '<p>Hello [RECEIVER_NAME],</p>  <p><a href="[SITE_URL]">[SITE_NAME]</a> is a new site for sharing and hosting personal videos. I have been using [SITE_NAME] to share videos with my friends and family. I would like to add you to the list of people I may share videos with.</p>  <p>Personal message from [SENDER_NAME]:</p>  <p>----------------------------</p> <p>[MESSAGE]</p> <p>----------------------------</p>  <p>To accept my friend request, click here:</p>  <p><a href="[VERIFY_URL]" target="_blank">[VERIFY_URL]</a></p>  <p>To visit [SITE_NAME], <a href="[SITE_URL]">click here</a>.</p>  <p>Thanks,</p> <p><a href="[SENDER_URL]">[SENDER_FNAME]</a></p>', 'Invite Friends');
INSERT INTO `emailinfo` VALUES ('invite_group_email', '[SENDER_NAME] has invited you to join YouTube group [GROUP_NAME]', '<p>Hello [RECEIVER_NAME],</p><p>[MESSAGE]</p><p>Click the link below to Join the group.</p><p><a href="[VERIFY_URL]">[VERIFY_URL]</a></p><p>To visit [SITE_NAME], <a href="[SITE_URL]">click here</a>.</p><p>Thanks,</p><p><a href="[SENDER_URL]">[SENDER_NAME]</a></p>', 'Group Invitation Mail');
INSERT INTO `emailinfo` VALUES ('recover_password', '[SITE_NAME] - New password activation', '<p>Hello [RECEIVER_NAME],</p><p>You are receiving this notification because you have (or someone pretending<br />to be you has from IP: [USER_IP]) requested a new password be sent for your account on &quot;[SITE_NAME]&quot;.</p><p> If you did not request this notification then please ignore it,<br />if you keep receiving it please contact the site administrator.&nbsp;</p>  <p>To use the new password you need to activate it. To do this click the link<br />provided below.</p>  <p><a href="[VERIFY_URL]">[VERIFY_URL]</a></p><p>If successful you will be able to login using the following user name and password:&nbsp;</p><p>User Name: [RECEIVER_NAME]<br />Password: [PASSWORD]</p><p>You can of course change this password yourself via the profile page. If<br />you have any difficulties please contact the site administrator.</p>    <p>Thank you,</p>  <p><a href="[SITE_URL]">[SITE_NAME]</a> Team</p>', 'User Password Reset');
INSERT INTO `emailinfo` VALUES ('user_email_change', 'Action Required to Verify Membership for [SITE_NAME]', 'Dear [USERNAME],<p>Since you have recently changed your email, we require that you verify your new email address. You will only need to visit the url once to have your account updated.</p><p>To complete reverification, please visit this url:</p><p><a href="[VERIFY_URL]">[VERIFY_URL]</a></p><p>If you are still having problems verifying please contact us. </p><p>All the best,</p><p><a href="[SITE_URL]">[SITE_NAME]</a> Team</p>', 'User Email Change');
INSERT INTO `emailinfo` VALUES ('friends_email', 'Your friend has registered in $site_name', '', 'mail to friends after registration');
INSERT INTO `emailinfo` VALUES ('recommend_video', '[SENDER_NAME] - sent you a video!', '<p>Hello,</p>  <p>[MESSAGE]</p>  <p>Click the link below to View the video.</p>  <p><a href="[VIDEO_URL]">[VIDEO_URL]</a></p>  <p>To visit [SITE_NAME], <a href="[SITE_URL]">click here</a>.</p>  <p>Thanks,</p><p>[SENDER_NAME]</p>  <a href="[SENDER_URL]">[SENDER_URL]</a> ', 'Recommend Video');
INSERT INTO `emailinfo` VALUES ('pm_notify', '[SITE_NAME] - You got a new private message', '<p>Hi, </p><p>You got a new private message from user [USERNAME]</p><p>------ </p><p>[MESSAGE]</p><p>------ </p><p>Thanking you, </p><p><a href="[SITE_URL]">[SITE_NAME]</a> </p>', 'PM Notify Email');
INSERT INTO `emailinfo` VALUES ('admin_change_password', '[SITE_NAME] -  Admin Password Changed', '<p>Hello  [ADMIN_NAME] ,  </p>\r\n\r\n<p>Admin login details for your web site is changed.</p>\r\n\r\n<p>Click the link below to activate Admin password:</p>\r\n\r\n<p><a href="[ACTIVATE_URL]">[ACTIVATE_URL]</a>&nbsp;</p>\r\n\r\n<p>User Name: [ADMIN_NAME]  </p>\r\n\r\n<p>Password: [ADMIN_PASSWORD]  </p>\r\n\r\n<p>IP address of user that changed the admin password is: [REMOTE_ADDR]  </p>\r\n\r\n<p>Thank you,  </p>\r\n\r\n<p><a href="[SITE_URL]">[SITE_NAME]</a> Team</p>', 'admin_change_password');
INSERT INTO `emailinfo` VALUES ('admin_signup_notify', '[SITE_NAME] New Signup - [USERNAME]', '<p>New User Signup</p><p>User Name : [USERNAME]<br />Email     : [USER_EMAIL]<br />IP        : [REMOTE_ADDR] <br />	BROWSER   :  [HTTP_USER_AGENT]<br />PROFILE: <a href="[USER_URL]">[USER_URL]</a></p>', 'Admin Signup Notify');
INSERT INTO `emailinfo` VALUES ('password_reset_admin', '[SITE_NAME] - Admin Password Change Request', '<p>Hello  [ADMIN_NAME], </p><p>You or someone requested for admin&nbsp;control&nbsp;panel password reset from IP address: [REMOTE_ADDR]</p><p>If you have not requested for password reset, just ignore the mail.</p><p>User Name: [ADMIN_NAME]<br />New Password: [PASSWORD]</p><p>Before using the&nbsp;new&nbsp;password,&nbsp;you&nbsp;need&nbsp;to&nbsp;activate&nbsp;it&nbsp;by&nbsp;clicking&nbsp;the&nbsp;link&nbsp;below. </p><p><a href="[VERIFY_URL]" target="_blank">[VERIFY_URL]</a> </p><p>Thank you,</p><p><a href="[SITE_URL]">[SITE_NAME]</a> Team</p>', 'Reset Admin Control Panel Password');
INSERT INTO `emailinfo` VALUES ('upload_notify_user', 'Your Video [VIDEO_TITLE] Uploaded to [SITE_NAME]', 'Thank you for video uploaded.  <p>USER ID: [USER_ID]</p><p>USER IP: [USER_IP]</p><p>USERNAME: [USERNAME]</p><p>TITLE: [VIDEO_TITLE]</p><p>DESCRIPTION: [DESCRIPTION]</p><p>KEYWORDS: [KEYWORDS]</p><p>CHANNEL: [CHANNELS]</p><p>TYPE: [TYPE]</p><p>VIDEO URL: [VIDEO_URL]</p>', 'Email send to user after video processed.');
INSERT INTO `emailinfo` VALUES ('resend_activation', '[SITE_NAME] - Activation Link', '<p>Dear [USERNAME],   </p><p>Click following link to activate your [SITE_NAME] account.  </p><p><a href="[VERIFY_URL]">[VERIFY_URL]</a>   </p><p>Thanking you,</p><p><a href="[SITE_URL]">[SITE_NAME] Team<br /></a></p>', 'Resend Activation Link');
INSERT INTO `emailinfo` VALUES ('upload_notify_admin', 'New Video Upload at [SITE_NAME] by [USERNAME]', '<p>USER ID: [USER_ID]</p><p>USER IP: [USER_IP]</p><p>USERNAME: [USERNAME]</p><p>TITLE: [TITLE]</p><p>DESCRIPTION: [DESCRIPTION]</p><p>KEYWORDS: [KEYWORDS]</p><p>CHANNEL: [CHANNELS]</p><p>TYPE: [TYPE]</p><p>VIDEO URL: [VIDEO_URL]</p>', 'Admin Notification for video upload');
